import "../src/App.scss";
import React from "react";
import "../src/components/Navbar/Navbar.scss";
import { motion, AnimatePresence } from "framer-motion";
function App() {
  const [navbarState, setNavbarState] = React.useState(0);
  function handleState(stateNumber) {
    setNavbarState(stateNumber);
  }
  const buttonRef = React.useRef();
  function handleClick() {
    if (currentMode == "Dark") setMode("Light");
    else setMode("Dark");
    if (document.body.style.backgroundColor == "white")
      document.body.style.backgroundColor = "black";
    else document.body.style.backgroundColor = "white";
    if (document.body.style.color === "white")
      document.body.style.color = "black";
    else document.body.style.color = "white";
  }
  const [currentMode, setMode] = React.useState("Dark");
  return (
    <div className="App">
      <nav>
        <h1>PYQ.Net</h1>
        <ul>
          <li onClick={() => handleState(0)}>Home</li>
          <li onClick={() => handleState(1)}>LogIn/SignUp</li>
          <li onClick={() => handleState(2)}>Contact Us</li>
          <li onClick={() => handleState(3)}>User Settings</li>
          <li
            className="dark_button"
            onClick={() => handleClick()}
            ref={buttonRef}
          >
            {currentMode}
          </li>
        </ul>
      </nav>
      <section>
        <AnimatePresence>
          {navbarState == 0 ? (
            <React.Fragment>
              <h1 className="uni_h1">Universities: </h1>
              <Home />
            </React.Fragment>
          ) : navbarState == 1 ? (
            <LogIn />
          ) : navbarState == 2 ? (
            <ContactUs />
          ) : (
            <UserSetting />
          )}
        </AnimatePresence>
      </section>
    </div>
  );
}

export default App;

const Home = () => {
  return (
    <motion.div animate={{y: 0}} exit={{y: 100}} initial={{y: 100}} transition={{duration: 0.8, ease: "easeInOut"}} className="home_div">
      <div className="new_button"><h1>Add New:</h1></div>
      <div className="cardDiv">
        <UniversityCard />  
      </div> 
    </motion.div>
  );
};

const LogIn = () => {
  return <div></div>;
};

const ContactUs = () => {
  return <div></div>;
};

const UserSetting = () => {
  return <div></div>;
};

const UniversityCard = () => {
  return (
    <motion.div whileHover={{ scale: 1.05 }} className="uni_card">
      <div className="uni_card_img_div"></div>
      <h1>Chitkar University</h1>
    </motion.div>
  );
};
